    public static class AOC_2015_Day01_Answer
    {
        public static int CalculateFloor(string inputData) =>
            inputData.Count(x => x == '(') -
            inputData.Count(x => x == ')');

        internal static object CalculateIteration(string inputData) =>
            inputData.AggregateUntil(0, (curr, x) => 
                x switch
                {
                    _ when x == '(' => curr + 1,
                    _ when x == ')' => curr - 1
                }, -1);
    }

